import NavBar from './components/navBar';
import RDC from './components/SVGs/RDC';
import SS_SOL from './components/SVGs/SS_SOL';
import NV_BAT from './components/SVGs/NV_BAT';
import FloorsButton from './components/floors_button';
import Buttons from './components/Buttons';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';


import './App.css';
class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      view: true
    };
    this.handleClick = this.handleClick.bind(this);
}

handleClick(view) {
  this.setState( {view} )
  console.log(this.state);
}

  render (){
    const { view } = this.state;
    return (

      <div className="App">
      <NavBar className="NavBar"/>
      <hr />

      <div >

      <ButtonGroup
        orientation="vertical"
        color="secondary"
        aria-label="vertical contained primary button group"
        variant="contained"
      >
        <Button onClick={() => this.handleClick(true)}>Sous Sol</Button>
        <Button onClick={() => this.handleClick(false)}>RDC</Button>
        <Button onClick={() => this.handleClick(false)}>Nouv Bat</Button>

        {/* {view === true ? <SS_SOL /> : <RDC /> } */}
        {view === true ? <SS_SOL /> : <NV_BAT /> }
        {/* {view === true ? <RDC /> : <NV_BAT /> } */}

      </ButtonGroup>

    </div>
    </div>  

    )

  }
}

export default App;
