import React from 'react';

import RDC from './SVGs/RDC';
import SS_SOL from './SVGs/SS_SOL';
import NV_BAT from './SVGs/NV_BAT';
import FloorsButton from './floors_button';


 props => (


    <div >

      <ButtonGroup
        orientation="vertical"
        color="secondary"
        aria-label="vertical contained primary button group"
        variant="contained"
      >
        <Button value={1} onClick={props.clickBtn}>Sous Sol</Button>
        <Button value={2} onClick={props.clickBtn}>RDC</Button>
        <Button value={3} onClick={props.clickBtn}>Nouv Bat</Button>
      </ButtonGroup>

    </div>
  );

class Buttons extends React.Component {
  state = {
    renderView: 0
  };

  clickBtn = e => {
    this.setState({
      renderView: +e.target.value
    });
  };

  render (){
    switch (this.state.renderView) {
      case 1:
        return <SS_SOL />;
      case 2:
        return <RDC />;
      case 3:
      return <NV_BAT />;

      default:
        return <FloorsButton clickBtn={this.clickBtn} />;
    }

  }
}

export default Buttons;
